import React, { Component } from 'react';
import axios from '../../../axios';
import Aux from '../../../hoc/Auxiliary/Auxiliary';
import Spinner from '../../UI/Spinner/Spinner';
import Button from '../../UI/Button/Button';

class SalespersonEdit extends Component {

    state = {
        firstName: null,
        lastName: null,
        address: null,
        phone: null,
        loading: false,
        error: false
    }

    componentDidMount () {
        // axios.get('/api/Salespersons/' + this.props.id)
        //     .then(response => {
        //         this.setState({ salesperson: response.data });
        //     })
        //     .catch(error => {
        //         console.log(error);
        //         this.setState({ error: true })
        //     });
    }

    render() {
        let summary = this.state.error ? <p>Data can't be loaded</p> : <Spinner />;
        if (this.state.salesperson) {
            summary = (
                <Aux>
                    <h3>Edit Salesperson</h3>
                    <div>
                        First Name: <input type="text" value={this.state.salesperson.FirstName} />
                    </div>
                    <div>
                        Last Name: <input type="text" value={this.state.salesperson.LastName} />
                    </div>
                    <div>
                        Address: <textarea>{this.state.salesperson.Address}</textarea>
                    </div>
                    <Button 
                        btnType="Danger"
                        clicked={this.props.editCancelled}>CANCEL</Button>
                    <Button 
                        btnType="Success"
                        clicked={this.props.editContinued}>CONTINUE</Button>
                </Aux>
            );
        }

        return (
            <Aux>
                {summary}
            </Aux>
        );
    }
}

export default SalespersonEdit;
