import React, { Component } from 'react';
import axios from '../../../axios';
import Aux from '../../../hoc/Auxiliary/Auxiliary';
import Spinner from '../../UI/Spinner/Spinner';
import Button from '../../UI/Button/Button';

class SalespersonEdit extends Component {

    state = {
        firstName: null,
        lastName: null,
        address: null,
        phone: null,
        loading: false,
        error: false
    }

    componentDidMount () {
        // axios.get('/api/Salespersons/' + this.props.id)
        //     .then(response => {
        //         this.setState({ salesperson: response.data });
        //     })
        //     .catch(error => {
        //         console.log(error);
        //         this.setState({ error: true })
        //     });
    }

    render() {
        return (
            <Aux>
                <h3>Edit Salesperson</h3>
                <div>
                    First Name: <input type="text" value={this.state.firstName} />
                </div>
                <div>
                    Last Name: <input type="text" value={this.state.lastName} />
                </div>
                <div>
                    Address: <textarea>{this.state.address}</textarea>
                </div>
                <Button 
                    btnType="Danger"
                    clicked={this.props.editCancelled}>CANCEL</Button>
                <Button 
                    btnType="Success"
                    clicked={this.props.editContinued}>CONTINUE</Button>
            </Aux>
        );
    }
}

export default SalespersonEdit;
