import React, { Component, ReactDOM } from 'react';
import axios from '../../../../axios';
import Aux from '../../../../hoc/Auxiliary/Auxiliary';
import Spinner from '../../../UI/Spinner/Spinner';
import Button from '../../../UI/Button/Button';
import classes from './SalespersonEdit.module.css';
import Input from '../../../UI/Input/Input';

class SalespersonEdit extends Component {

    state = {
        salesperson: null,
        error: false,
        salespersonForm: {
            firstName: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'First Name'
                },
                value: ''
            },
            lastName: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Last Name'
                },
                value: ''
            },
            address: {
                elementType: 'input',
                elementConfig: {
                    type: 'textarea',
                    placeholder: 'Full Address'
                },
                value: ''
            },
            phone: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Phone'
                },
                value: ''
            },
            manager: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Manager'
                },
                value: ''
            }
        }
    }

    componentDidMount () {
        console.log('[SalespersonEdit] props = ', this.props);
        const pathname = this.props.location.pathname;
        axios.get('/api/Salespersons/' + pathname.substring(pathname.lastIndexOf('/'), pathname.length))
            .then(response => {
                this.setState({ salesperson: response.data });
            })
            .catch(error => {
                console.log(error);
                this.setState({ error: true })
            });
    }

    submitHandler = ( event ) => {
        this.state.inputs.map( function(item, i) {
          console.log(ReactDOM.findDOMNode(this.refs['input-' + i]).value);
        }.bind(this))
    }

    inputChangedHandler = (event) => {
        console.log(event.target.value);
    }

    render() {
        let form = null;
        const formElementsArray = [];
        for (let key in this.state.salespersonForm) {
            formElementsArray.push({
                id: key,
                config: this.state.salespersonForm[key]
            });
        }
        if (this.state.salesperson) {
            form = (
                <ul className={ classes.Form }>
                    <li>
                        {formElementsArray.map(formElement => (
                            <ul key={formElement.id}>
                                <li>{formElement.config.elementConfig.placeholder}:</li>
                                <li>
                                    <Input 
                                        elementType={formElement.config.elementType}
                                        elementConfig={formElement.config.elementConfig}
                                        value={formElement.config.value} 
                                        changed={this.inputChangedHandler} />
                                </li>
                            </ul>
                        ))}
                    </li>
                </ul>
            );
        } else if (this.state.error) {
            form = (
                <div>An error has occurred.</div>
            );
        } else {
            form = <Spinner />;
        }
        return (
            <Aux>
                <form onSubmit={this.submitHandler}>
                    <h3>Edit Salesperson</h3>
                    {form}
                    <Button 
                        btnType="Danger"
                        clicked={this.props.editCancelled}>CANCEL</Button>
                    <Button 
                        btnType="Success"
                        clicked={this.props.editContinued}>CONTINUE</Button>
                </form>
            </Aux>
        );
    }
}

export default SalespersonEdit;
