import React, { Component } from 'react';
import Aux from '../../../hoc/Auxiliary/Auxiliary';
import axios from '../../../axios';
import Salesperson from './Salesperson';
import Spinner from '../../UI/Spinner/Spinner';
import Modal from '../../UI/Modal/Modal';
import SalespersonEdit from './SalespersonEdit';
import Button from '../../UI/Button/Button';

class Salespersons extends Component {

    state = {
        salespersons: null,
        selectedSalesperson: null,
        id: null,
        firstName: '',
        lastName: '',
        address: '',
        phone: '',
        startDate: null,
        terminationDate: null,
        manager: '',
        loading: true,
        error: null,
        editing: false
    };

    componentDidMount () {
        axios.get('/api/Salespersons')
            .then(response => {
                this.setState({ salespersons: response.data });
            })
            .catch(error => {
                this.setState({ error: true })
            });
    }

    editCancelHandler = () => {
        this.setState({editing: false});
    }

    editContinueHandler = () => {
        this.setState({editing: true});
    }

    salesPersonEditHandler = (salesperson) => {
        this.setState({selectedSalesperson: salesperson, editing: true})
    }

    render() {
        let salesPersonEdit = null;
        let list = this.state.error ? <p>Data can't be loaded</p> : <Spinner />;
        if (this.state.salespersons) {
            list = (
                <Aux>
                    {this.state.salespersons.map(salesperson => {
                        console.log("salesperson: ", salesperson);
                        return (
                            <Aux key={salesperson.Id}>
                                <Salesperson 
                                    id={salesperson.Id} 
                                    firstName={salesperson.FirstName}
                                    lastName={salesperson.LastName} 
                                    address={salesperson.Address} 
                                    phone={salesperson.Phone}   />
                                <Button btnType="Success" clicked={this.salesPersonEditHandler(salesperson)}>Edit</Button>
                            </Aux>
                        )
                    })}
                </Aux>
            );

        }
        if (this.state.selectedSalesperson) {            
            salesPersonEdit = <SalespersonEdit
                    id={this.state.selectedSalesperson.Id}
                    firstName={this.state.selectedSalesperson.FirstName}
                    lastName={this.state.selectedSalesperson.LastName}
                    address={this.state.selectedSalesperson.Address}
                    phone={this.state.selectedSalesperson.Address}
                    editCancelled={this.editCancelHandler}
                    editContinued={this.editContinueHandler} />
        }
        return (
            <Aux>
                <Modal 
                    show={this.state.editing}
                    modalClosed={this.editCancelHandler}>
                    {salesPersonEdit}
                </Modal>
                {list}
            </Aux>
        );
    }
}

export default Salespersons;
