import React, { Component } from 'react';
import Aux from '../../../hoc/Auxiliary/Auxiliary';
import axios from '../../../axios';
import Button from '../../UI/Button/Button';
import SalespersonEdit from './SalespersonEdit';

class Salesperson extends Component {

    state = {
        id: null,
        firstName: '',
        lastName: '',
        address: '',
        phone: '',
        startDate: null,
        terminationDate: null,
        manager: '',
        loading: '',
        error: null
    };

    componentDidMount () {
        axios.get('/api/Salespersons/' + this.props.id)
            .then(response => {
                this.setState({
                    id: response.data.Id,
                    firstName: response.data.FirstName,
                    lastName: response.data.LastName,
                    address: response.data.Address,
                    phone: response.data.Phone,
                    startDate: response.data.StartDate,
                    terminationDate: response.data.TerminationDate,
                    manager: response.data.Manager
                })
                //this.setState({ ingredients: response.data });
            })
            .catch(error => {
                this.setState({ error: true })
            });
    }

    updatePersonHandler = ( id ) => {
        this.props.history.push({
            pathname: '/' + id
        })
    }

    render() {
        return (
            <Aux>
                <div>
                    <div>
                        <div>First Name: {this.state.firstName}</div>
                        <div>Last Name: {this.state.lastName}</div>
                        <div>Address: {this.state.address}</div>
                        <div>Phone: {this.state.phone}</div>
                    </div>
                    <div>
                        <Button clicked={this.updatePersonHandler(this.state.id)}>Edit</Button>
                    </div>
                </div>
            </Aux>
        );
    }
}

export default Salesperson;
