import React, { Component } from 'react';
import Aux from '../../../hoc/Auxiliary/Auxiliary';
import axios from '../../../axios';
import Salesperson from './Salesperson';
import Spinner from '../../UI/Spinner/Spinner';

class Salespersons extends Component {

    state = {
        salespersons: null,
        id: null,
        firstName: '',
        lastName: '',
        address: '',
        phone: '',
        startDate: null,
        terminationDate: null,
        manager: '',
        loading: true,
        error: null
    };

    componentDidMount () {
        axios.get('/api/Salespersons')
            .then(response => {
                //console.log(response);
                this.setState({ salespersons: response.data });
                //this.setState({ ingredients: response.data });
            })
            .catch(error => {
                this.setState({ error: true })
            });
    }

    render() {
        let list = this.state.error ? <p>Data can't be loaded</p> : <Spinner />;
        if (this.state.salespersons) {
            list = (
                <Aux>
                    {this.state.salespersons.map(salesperson => {
                        return (
                            <Aux>
                            <Salesperson key={salesperson.id} id={salesperson.id} />
                            </Aux>
                        )
                    })}
                </Aux>
            )
        }
        return (
            <Aux>
                {list}
            </Aux>
        );
    }
}

export default Salespersons;
