import React, { Component } from 'react';
import axios from '../../../../axios';
import Aux from '../../../../hoc/Auxiliary/Auxiliary';
import Spinner from '../../../UI/Spinner/Spinner';
import Button from '../../../UI/Button/Button';
import { Route } from 'react-router-dom';

class SalespersonEdit extends Component {

    state = {
        salesperson: null
    }

    componentDidMount () {
        console.log('[SalespersonEdit] props = ', this.props);
        axios.get('/api/Salespersons/' + this.props.match.params.id)
            .then(response => {
                this.setState({ salesperson: response.data });
            })
            .catch(error => {
                console.log(error);
                this.setState({ error: true })
            });
    }

    render() {
        return (
            <Aux>
                <h3>Edit Salesperson</h3>
                <ul>
                    <li>First Name:</li>
                    <li><input type="text" defaultValue={this.state.salesperson.FirstName} /></li>
                    <li>Last Name:</li>
                    <li><input type="text" defaultValue={this.state.salesperson.LastName} /></li>
                </ul>
                <Button 
                    btnType="Danger"
                    clicked={this.props.editCancelled}>CANCEL</Button>
                <Button 
                    btnType="Success"
                    clicked={this.props.editContinued}>CONTINUE</Button>
            </Aux>
        );
    }
}

export default SalespersonEdit;
