import React, { Component, ReactDOM } from 'react';
import axios from '../../../../axios';
import Aux from '../../../../hoc/Auxiliary/Auxiliary';
import Spinner from '../../../UI/Spinner/Spinner';
import Button from '../../../UI/Button/Button';
import classes from './SalesNew.module.css';

class SalesEdit extends Component {

    state = {
        products: null,
        sales: {
            product: null,
            salesperson: null,
            customer: null,
            date: null
        },
        error: false
    }

    componentDidMount () {
        axios.get('/api/Products')
            .then(response => {
                this.setState({ products: response.data });
            })
            .catch(error => {
                this.setState({ error: true })
            });
    }
    inputChangeHandler = (event, id) => {
        // var stateObject = function() {
        //     let returnObj = {
        //         ...this.state
        //     };
        //     returnObj[this.target.name] = this.target.value;
        //        return returnObj;
        //   }.bind(event)();
        // console.log(this.state);
        // this.setState({ stateObject })
        console.log(this.state);
        //this.setState({ [event.target.name]: event.target.value});
        
        const updatedSale = {
            ...this.state.sales
        };
        const updatedFormElement = {
            ...updatedSale[id]
        };
        //updatedFormElement.value = event.target.value;
        //console.log(typeof(updatedFormElement));
        updatedSale[id] = updatedFormElement;
        this.setState({ sale: updatedSale });
    }

    submitHandler = ( event ) => {
        this.state.inputs.map( function(item, i) {
          console.log(ReactDOM.findDOMNode(this.refs['input-' + i]).value);
        }.bind(this))

        event.preventDefault();
        this.setState({ loading: true });
        const formData = {};
        for (let formElementIdentifier in this.state.salesForm) {
            formData[formElementIdentifier] = this.state.salesForm[formElementIdentifier].value;
        }
        // const submission = {
        //     formData: formData
        // }
        const pathname = this.props.location.pathname;
        console.log(formData);
        axios.put(
            '/api/Sales/' + pathname.substring(pathname.lastIndexOf('/'), pathname.length), formData)
            .then(response => {
                this.setState({ loading: false });
                this.props.history.push('/');
            })
            .catch(error => {
                console.log(error);
                this.setState({ loading: false, error: true })
            });
    }

    editCancelled = () => {
        this.props.history.push('/sales');
    }

    render() {
        let products = { value: '', label: '--- Please Select a Product---'}
        if (this.state.products) {
            for (let product in this.state.products) {
                products.push({ value: product.Id, label: product.Name })
            }
        }
        console.log(products);
        let form = (
            <ul className={ classes.Form }>
                <li>
                    <ul>
                        <li>Product:</li>
                        <li>
                            <select name="Product"
                                defaultValue=""
                                onChange={(event) => this.inputChangeHandler(event, 'Product')}>
                                {/* {products.map(p => {
                                        return (
                                            <option value={p.value}>{p.label}</option>
                                        )
                                    }
                                )} */}
                            </select>
                        </li>
                    </ul>
                    <ul>
                        <li>Customer:</li>
                        <li><input type="text" name="Customer" defaultValue="" onChange={this.inputChangeHandler} /></li>
                    </ul>
                    <ul>
                        <li>Salesperson:</li>
                        <li><input type="text" name="Salesperson" defaultValue="" onChange={this.inputChangeHandler} /></li>
                    </ul>
                    <ul>
                        <li>Sale Date:</li>
                        <li><input type="text" name="SalesDate" defaultValue="" onChange={this.inputChangeHandler} /></li>
                    </ul>
                </li>
            </ul>
        );
        return (
            <Aux>
                <form onSubmit={this.submitHandler}>
                    <h3>New Sale</h3>
                    {form}
                    <Button 
                        btnType="Danger"
                        clicked={this.editCancelled}>CANCEL</Button>
                    <Button 
                        btnType="Success"
                        clicked={this.props.editContinued}>CONTINUE</Button>
                </form>
            </Aux>
        );
    }
}

export default SalesEdit;
