import React, { Component } from 'react';
import axios from '../../../../axios';
import Aux from '../../../../hoc/Auxiliary/Auxiliary';
import Spinner from '../../../UI/Spinner/Spinner';
import Button from '../../../UI/Button/Button';
import { Route } from 'react-router-dom';

class SalespersonEdit extends Component {

    componentDidMount () {
        console.log(this.props.match.params);
        axios.get('/api/Salespersons/' + this.props.id)
            .then(response => {
                this.setState({ salesperson: response.data });
            })
            .catch(error => {
                console.log(error);
                this.setState({ error: true })
            });
    }

    render() {
        return (
            <Aux>
                <h3>Edit Salesperson</h3>
                <div>
                    First Name: <input type="text" defaultValue={this.props.firstName} />
                </div>
                <div>
                    Last Name: <input type="text" defaultValue={this.props.lastName} />
                </div>
                <div>
                    Address: <textarea>{this.props.address}</textarea>
                </div>
                <Button 
                    btnType="Danger"
                    clicked={this.props.editCancelled}>CANCEL</Button>
                <Button 
                    btnType="Success"
                    clicked={this.props.editContinued}>CONTINUE</Button>
            </Aux>
        );
    }
}

export default SalespersonEdit;
